import React, { Component } from 'react';
import {
    Button, Container, CssBaseline, Grid, Typography, 
    Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions
} from '@material-ui/core';
import { 
    BrowserRouter as Router, Switch, Route, Link as RouterLink 
} from "react-router-dom";
import Publish from '@material-ui/icons/Publish';
import { withStyles } from '@material-ui/core/styles';


const styles = theme => ({
    root: {
        // backgroundColor: '#fde3a7',
        position: 'relative',
        display: 'flex',
        alignItems: 'center',
        height: '90vh',
        minHeight: 500,
        maxHeight: 1300,
    },
    container: {
        marginTop: theme.spacing(3),
        marginBottom: theme.spacing(14),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
        button: {
        fontSize: 20
    },
    icon: {
        marginRight: theme.spacing(2)
    },
    image: {
        height: 150,
    }
});

const RefLink = React.forwardRef((props, ref) => <RouterLink innerRef={ref} {...props} />);

class Upload extends Component {

    constructor(props) {
        super(props);

        this.state = {isOpen: false};
        this.handleClickOpen = this.handleClickOpen.bind(this);
    }

    componentDidMount() {
        this.handleClickOpen = () => this.open = true;
    }
    // handleClickOpen = () => {
    //     this.open = true;
    // };

    handleClose = () => {
        this.open = false;
    };

    handleImageClick = (e, data) => {
        console.log(e.target);
        e.target.style.border = 'solid';
        
    }

    render() {
        const { classes } = this.props;

        return (
            // Guidelines for ideal image quality/characteristics
            // Dialog options for user provided features

            <React.Fragment>
                <CssBaseline />

                <section className={classes.root}>
                    <Container maxWidth="sm" className={classes.container}>
                        <Grid container justify="center">
                            <Grid item>
                                <Button variant="contained" color="primary" size="large" onClick={this.handleClickOpen} className={classes.button}>
                                    <Publish fontSize="large" className={classes.icon}/>
                                    Upload
                                </Button>

                                
                            </Grid>
                        </Grid>
                    </Container>
                </section>

                <Dialog
                    fullWidth={true}
                    maxWidth={'md'}
                    open={this.open}
                    onClose={this.handleClose}
                    aria-labelledby="dialog-title"
                >
                    <DialogTitle id="dialog-title">Select an Image to Upload...</DialogTitle>

                    <DialogContent>
                        <Container maxwidth="sm" className={classes.container}>
                            <Grid container justify="center">
                                <Grid sm={4}>
                                    <img
                                        src="image-placeholder.jpg"
                                        alt="upload-img"
                                        className={classes.image}
                                        onClick={this.handleImageClick}
                                    />
                                </Grid>

                                <Grid sm={4}>
                                    <img
                                        src="image-placeholder.jpg"
                                        alt="upload-img"
                                        className={classes.image}
                                    />
                                </Grid>

                                <Grid sm={4}>
                                    <img
                                        src="image-placeholder.jpg"
                                        alt="upload-img"
                                        className={classes.image}
                                    />
                                </Grid>
                            </Grid>
                        </Container>

                    </DialogContent>

                    <DialogActions>
                        <Button onClick={this.handleClose} color="secondary">
                            Cancel
                        </Button>

                        <Button component={RefLink} to="/analyzing" color="primary">
                            Confirm
                        </Button>
                    </DialogActions>
                </Dialog>
            </React.Fragment>
        )
    }
   
}

export default withStyles(styles)(Upload)
