import React, { Component } from 'react';
import {
    Button, Container, CssBaseline, Grid, Typography,
    Fade
} from '@material-ui/core';
import { 
    Link as RouterLink 
} from "react-router-dom";
import { withStyles } from '@material-ui/core/styles';
import { Linear } from 'gsap';
import ScrollMagic from 'scrollmagic';
import 'animation.gsap'
import 'debug.addIndicators'

const styles = theme => ({
    root: {
        // backgroundColor: '#fde3a7',
        backgroundImage: `url('hero-bg-1.png')`,
        backgroundSize: 'cover',
        position: 'relative',
        display: 'flex',
        alignItems: 'center',
        // [theme.breakpoints.up('sm')]: {
        //     height: '85vh',
        //     minHeight: 500,
        //     maxHeight: 1300,
        // },

        // width: '100%',
        height: '85vh',
        minHeight: 500,
        overflowX: 'hidden',
        overflowY: 'hidden',
        // perspective: '8px',
        // perspectiveOrigin: '0%',
        // display: 'flex',
    },
    container: {
        marginTop: theme.spacing(3),
        marginBottom: theme.spacing(14),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    // foreground: {
    //     marginTop: 'auto',
    //     marginBottom: '50px',
    //     transformOrigin: '0',
    //     transform: 'translateZ(3px) scale(0.625)',
    // },

    parallaxParent: {
        height: '100vh',
		overflow: 'hidden',
    },

    parallaxChild: {
        height: '200%',
        position: 'relative',
        top: '-100%',
        backgroundImage: `url('hero-bg-1.png')`,
        // transform: `translate(0%, 55.8358%) translate3d(0px, 0px, 0px)`,
    },
});

const RefLink = React.forwardRef((props, ref) => <RouterLink innerRef={ref} {...props} />);

class ProductHero extends Component {
    
    constructor(props) {
        super(props);
        this.controller = new ScrollMagic.Controller({globalSceneOptions: {triggerHook: "onEnter", duration: "200%"}});
    }


    componentDidMount() {
        // // build tween
        // var tween = new TimelineMax ()
        // .add([
        //     TweenMax.fromTo("#myElement", 1, {top: 0}, {top: -350, ease: Linear.easeNone}),
        // ]);


        new ScrollMagic.Scene({
            triggerElement: '#scrollStarts',
        })
        .setTween('#parallax1', {y: '80%', ease: Linear.easeNone})
        .addIndicators() // add indicators (requires plugin)
        .addTo(this.controller); // assign the scene to the controller

      }

    render() {
        const { classes } = this.props;

        return (
            <React.Fragment>
                <CssBaseline />


                <section className={classes.root}>
                <div id="scrollStarts"></div>
                    <Container maxWidth="sm" className={classes.container}>
                        <div id="parallax1" className={classes.parallaxParent}>
                            <div className={classes.parallaxChild}>HELLO</div>
                        </div>
                        

                        {/* <Fade timeout={1000} in={true}>
                            <div className={classes.foreground}>
                                <Typography id="myElement" component="h1" align="center" variant="h2" color="textPrimary" gutterBottom> 
                                    Header 1
                                </Typography>
                                <Typography variant="h5" align="center" color="textSecondary" paragraph>
                                    Something short and leading about the application.
                                </Typography>
                                <div className={classes.heroButtons}>
                                    <Grid container justify="center">
                                        <Grid item>
                                            <Button variant="contained" color="primary" component={RefLink} to="/upload">
                                                Get Started
                                            </Button>
                                        </Grid>
                                    </Grid>
                                </div>
                            </div>
                        </Fade> */}
                    </Container>
                </section>
            </React.Fragment>
        )
    }
}

export default withStyles(styles)(ProductHero)
