import React from 'react';
import {
    Button, Container, CssBaseline, Grid, Typography,
    Fade
} from '@material-ui/core';
import { 
    BrowserRouter as Router, Switch, Route, Link as RouterLink 
} from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    root: {
        backgroundColor: '#fde3a7',
        position: 'relative',
        display: 'flex',
        alignItems: 'center',
        [theme.breakpoints.up('sm')]: {
            height: '85vh',
            minHeight: 500,
            maxHeight: 1300,
        },

        width: '100%',
        height: '100%',
        overflowX: 'hidden',
        overflowY: 'hidden',
        perspective: '8px',
        perspectiveOrigin: '0%',
        display: 'flex',
    },
    container: {
        marginTop: theme.spacing(3),
        marginBottom: theme.spacing(14),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    foreground: {
        marginTop: 'auto',
        marginBottom: '50px',
        transformOrigin: '0',
        transform: 'translateZ(3px)',
    },
}));

const RefLink = React.forwardRef((props, ref) => <RouterLink innerRef={ref} {...props} />);

export default function ProductHero(props) {
    const classes = useStyles();
    const [checked, setChecked] = React.useState(true);

    return (
        <React.Fragment>
            <CssBaseline />

            <section className={classes.root}>
                <Container maxWidth="sm" className={classes.container}>
                    <Fade timeout={1000} in={checked}>
                        <div className={classes.foreground}>
                            <Typography component="h1" align="center" variant="h2" color="textPrimary" gutterBottom>
                                Header 1
                            </Typography>
                            <Typography variant="h5" align="center" color="textSecondary" paragraph>
                                Something short and leading about the application.
                            </Typography>
                            <div className={classes.heroButtons}>
                            <Button variant="contained" color="primary" component={RefLink} to="/upload">
                                            Get Started
                                        </Button>
                            </div>
                        </div>
                    </Fade>
                </Container>
            </section>
        </React.Fragment>
    )
}
