import React, { Component, useRef } from 'react';
import {
    Button, Container, CssBaseline, Grid, Typography,
    Fade
} from '@material-ui/core';
import { 
    BrowserRouter as Router, Switch, Route, Link as RouterLink 
} from "react-router-dom";
import { withStyles } from '@material-ui/core/styles';
import {TweenMax, Linear} from 'gsap';
import ScrollMagic from 'scrollmagic';

const styles = theme => ({
    root: {
        backgroundColor: '#fde3a7',
        position: 'relative',
        display: 'flex',
        alignItems: 'center',
        [theme.breakpoints.up('sm')]: {
            height: '85vh',
            minHeight: 500,
            maxHeight: 1300,
        },

        // width: '100%',
        // height: '100%',
        // overflowX: 'hidden',
        // overflowY: 'hidden',
        // perspective: '8px',
        // perspectiveOrigin: '0%',
        // display: 'flex',
    },
    container: {
        marginTop: theme.spacing(3),
        marginBottom: theme.spacing(14),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    // foreground: {
    //     marginTop: 'auto',
    //     marginBottom: '50px',
    //     transformOrigin: '0',
    //     transform: 'translateZ(3px) scale(0.625)',
    // },
});

const RefLink = React.forwardRef((props, ref) => <RouterLink innerRef={ref} {...props} />);

class ProductHero extends Component {
    
    constructor(props) {
        super(props);

        this.state = {
            completed: 0,
        };
    }

    // const classes = useStyles();
    // const [checked, setChecked] = React.useState(true);

    // function scaleUp() {
    //     TweenMax.to(heroContent, 1, {
    //         scale: 1.25,
    //         ease: Linear.ease
    //     });
    // }

    render() {
        const { classes } = this.props;

        return (
            <React.Fragment>
                <CssBaseline />

                <section className={classes.root}>
                    <Container maxWidth="sm" className={classes.container}>
                        <Fade timeout={1000} in={true}>
                            <div className={classes.foreground}>
                                <Typography component="h1" align="center" variant="h2" color="textPrimary" gutterBottom> 
                                    Header 1
                                </Typography>
                                <Typography variant="h5" align="center" color="textSecondary" paragraph>
                                    Something short and leading about the application.
                                </Typography>
                                <div className={classes.heroButtons}>
                                    <Grid container justify="center">
                                        <Grid item>
                                            <Button variant="contained" color="primary" component={RefLink} to="/upload">
                                                Get Started
                                            </Button>
                                        </Grid>
                                    </Grid>
                                </div>
                            </div>
                        </Fade>
                    </Container>
                </section>
            </React.Fragment>
        )
    }
}

export default withStyles(styles)(ProductHero)
