import React from 'react';
import {
    Button, Container, CssBaseline, Grid, LinearProgress, Typography
} from '@material-ui/core';
import { 
    BrowserRouter as Router, Switch, Route, Link as RouterLink, Redirect
} from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    root: {
        backgroundColor: '#fde3a7',
        position: 'relative',
        display: 'flex',
        alignItems: 'center',
        [theme.breakpoints.up('lg')]: {
          height: '90vh',
          minHeight: 500,
          maxHeight: 1300,
        },
      },
      container: {
        marginTop: theme.spacing(3),
        marginBottom: theme.spacing(14),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      },
      button: {
        fontSize: 20
      },
      icon: {
          marginRight: theme.spacing(2)
      },
      image: {
          height: 150
      }
}));

const RefLink = React.forwardRef((props, ref) => <RouterLink innerRef={ref} {...props} />);

export default function Analyzing() {
    const classes = useStyles();
    const [completed, setCompleted] = React.useState(0);

    React.useEffect(() => {
      function progress() {
        setCompleted(oldCompleted => {
          if (oldCompleted === 100) {
            return <Redirect to="/image-assessment" />;
          }
          const diff = Math.random() * 10;
          return Math.min(oldCompleted + diff, 100);
        });
      }
  
      const timer = setInterval(progress, 500);
      return () => {
        clearInterval(timer);
      };
    }, []);
  

    return (
        <React.Fragment>
            <CssBaseline />

            <section className={classes.root}>
                <Container className={classes.container}>
                    <Grid container justify="center">
                        <Grid item>
                            <Typography variant="h3" gutterBottom>
                                The walruses are hard at work!
                            </Typography>
                        </Grid>
                    </Grid>

                    <Grid container justify="center">
                        <Grid item sm={12}>
                            <LinearProgress variant="determinate" value={completed} />
                        </Grid>
                    </Grid>
                </Container>
            </section>
        </React.Fragment>
    )
}
