import React from 'react';
import {
    Button, Container, CssBaseline, Grid, Typography, 
} from '@material-ui/core';
import Publish from '@material-ui/icons/Publish';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    root: {
        color: theme.palette.common.white,
        position: 'relative',
        display: 'flex',
        alignItems: 'center',
        [theme.breakpoints.up('sm')]: {
          height: '80vh',
          minHeight: 500,
          maxHeight: 1300,
        },
      },
      container: {
        marginTop: theme.spacing(3),
        marginBottom: theme.spacing(14),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      },
      button: {
        fontSize: 20
      },
      icon: {
          marginRight: theme.spacing(2)
      }
}));

export default function Upload() {
    const [open, setOpen] = React.useState(false);
    const classes = useStyles();

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <React.Fragment>
            <CssBaseline />

            {/* Hero unit */}
            <section className={classes.root}>
                <Container maxWidth="sm" className={classes.container}>
                    <Grid container justify="center">
                        <Grid item>
                            <Button variant="contained" color="primary" size="large" onClick={handleClickOpen}> className={classes.button}>
                                <Publish fontSize="large" className={classes.icon}/>
                                Upload
                            </Button>

                            <Dialog
                                fullWidth={fullWidth}
                                maxWidth={maxWidth}
                                open={open}
                                onClose={handleClose}
                                aria-labelledby="max-width-dialog-title"
                            >
                                <DialogTitle id="max-width-dialog-title">Optional sizes</DialogTitle>
                                <DialogContent>
                                <DialogContentText>
                                    You can set my maximum width and whether to adapt or not.
                                </DialogContentText>
                                <form className={classes.form} noValidate>
                                    <FormControl className={classes.formControl}>
                                    <InputLabel htmlFor="max-width">maxWidth</InputLabel>
                                    <Select
                                        value={maxWidth}
                                        onChange={handleMaxWidthChange}
                                        inputProps={{
                                        name: 'max-width',
                                        id: 'max-width',
                                        }}
                                    >
                                        <MenuItem value={false}>false</MenuItem>
                                        <MenuItem value="xs">xs</MenuItem>
                                        <MenuItem value="sm">sm</MenuItem>
                                        <MenuItem value="md">md</MenuItem>
                                        <MenuItem value="lg">lg</MenuItem>
                                        <MenuItem value="xl">xl</MenuItem>
                                    </Select>
                                    </FormControl>
                                    <FormControlLabel
                                    className={classes.formControlLabel}
                                    control={
                                        <Switch checked={fullWidth} onChange={handleFullWidthChange} value="fullWidth" />
                                    }
                                    label="Full width"
                                    />
                                </form>
                                </DialogContent>
                                <DialogActions>
                                <Button onClick={handleClose} color="primary">
                                    Close
                                </Button>
                                </DialogActions>
                            </Dialog>
                        </Grid>
                    </Grid>
                </Container>
            </section>
        </React.Fragment>
    )
}
