import React from 'react';
import {
    Button, Container, CssBaseline, Grid, Typography,
    Fade
} from '@material-ui/core';
import { 
    BrowserRouter as Router, Switch, Route, Link as RouterLink 
} from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    // root: {
    //     width: '100%',
    // },
    root: {
        position: 'relative',
        width: '100%',
        height: '100%',
        overflowX: 'hidden',
        overflowY: 'hidden',
        perspective: 8,
        perspectiveOrigin: '0%',
        display: 'flex',
    },
    background: {
        backgroundColor: '#fde3a7',
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        transform: `translateZ(0px)`,
    },
    foreground: {
        margeTop: 'auto',
        marginBottom: 50,
        transformOrigin: 0,
        transform: `translateZ(3px) scale(0.625)`,
    },  
    container: {
        marginTop: theme.spacing(3),
        marginBottom: theme.spacing(14),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
}));

const RefLink = React.forwardRef((props, ref) => <RouterLink innerRef={ref} {...props} />);

export default function ProductHero(props) {
    const classes = useStyles();
    const [checked, setChecked] = React.useState(true);

    return (
        <React.Fragment>
            <CssBaseline />

                <section className={classes.root}>
                    <Container className={classes.container}>
                        <div className={classes.parallax}>
                            <div className={classes.background}> 
                            </div>


                                <Fade timeout={1000} in={checked}>
                                    <div className={classes.foreground}>
                                        <Typography component="h1" align="center" variant="h2" color="textPrimary" gutterBottom>
                                            Header 1
                                        </Typography>
                                        <Typography variant="h5" align="center" color="textSecondary" paragraph>
                                            Something short and leading about the application.
                                        </Typography>
                                        <div className={classes.heroButtons}>
                                            <Grid container justify="center">
                                                <Grid item>
                                                    <Button variant="contained" color="primary" component={RefLink} to="/upload">
                                                        Get Started
                                                    </Button>
                                                </Grid>
                                            </Grid>
                                        </div>
                                    </div>
                                </Fade>
                        </div>
                    </Container>
                </section>
        </React.Fragment>
    )
}
