import React from 'react';
import {
    Button, Container, CssBaseline, Grid, Typography, 
    Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions
} from '@material-ui/core';
import { 
    BrowserRouter as Router, Switch, Route, Link as RouterLink 
} from "react-router-dom";
import Publish from '@material-ui/icons/Publish';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    root: {
        color: '#fde3a7',
        position: 'relative',
        display: 'flex',
        alignItems: 'center',
        [theme.breakpoints.up('sm')]: {
          height: '90vh',
          minHeight: 500,
          maxHeight: 1300,
        },
      },
      container: {
        marginTop: theme.spacing(3),
        marginBottom: theme.spacing(14),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      },
      button: {
        fontSize: 20
      },
      icon: {
          marginRight: theme.spacing(2)
      },
      image: {
          height: 150
      }
}));

const RefLink = React.forwardRef((props, ref) => <RouterLink innerRef={ref} {...props} />);

export default function Upload() {
    const [open, setOpen] = React.useState(false);
    const classes = useStyles();

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <React.Fragment>
            <CssBaseline />

            <section className={classes.root}>
                <Container maxWidth="sm" className={classes.container}>
                    <Grid container justify="center">
                        <Grid item>
                            <Button variant="contained" color="primary" size="large" onClick={handleClickOpen} className={classes.button}>
                                <Publish fontSize="large" className={classes.icon}/>
                                Upload
                            </Button>

                            
                        </Grid>
                    </Grid>
                </Container>
            </section>

            <Dialog
                fullWidth={true}
                maxWidth={'md'}
                open={open}
                onClose={handleClose}
                aria-labelledby="dialog-title"
            >
                <DialogTitle id="dialog-title">Select an Image to Upload...</DialogTitle>

                <DialogContent>
                    <Container maxwidth="sm" className={classes.container}>
                        <Grid container justify="center">
                            <Grid sm={4}>
                                <img
                                    src="image-placeholder.jpg"
                                    alt="upload-img"
                                    className={classes.image}
                                />
                            </Grid>

                            <Grid sm={4}>
                                <img
                                    src="image-placeholder.jpg"
                                    alt="upload-img"
                                    className={classes.image}
                                />
                            </Grid>

                            <Grid sm={4}>
                                <img
                                    src="image-placeholder.jpg"
                                    alt="upload-img"
                                    className={classes.image}
                                />
                            </Grid>
                        </Grid>
                    </Container>

                </DialogContent>

                <DialogActions>
                    <Button onClick={handleClose} color="secondary">
                        Cancel
                    </Button>

                    <Button component={RefLink} to="/image-assessment" color="primary">
                        Confirm
                    </Button>
                </DialogActions>
            </Dialog>
        </React.Fragment>
    )
}
